import './App.css';

// import { initializeApp } from "firebase/app";
import firebase from './firebase';
import {
  getFirestore,
  collection,
  onSnapshot,
  // getDocs,
  // getDoc,
  addDoc,
  deleteDoc,
  doc,
  // query,
  // where,
  // orderBy,
  serverTimestamp,
  updateDoc,

} from "firebase/firestore";
import {
  getAuth,
  createUserWithEmailAndPassword,
} from 'firebase/auth'

// const firebaseConfig = {
//   apiKey: "AIzaSyD2tg3o7WL3TWHC6CAG2t1pxgQyEmVgapA",
//   authDomain: "fern-446ae.firebaseapp.com",
//   projectId: "fern-446ae",
//   storageBucket: "fern-446ae.appspot.com",
//   messagingSenderId: "842630607601",
//   appId: "1:842630607601:web:5bb28413839b6efd536f50",
//   measurementId: "G-D0VPDXJFYG",
// };
//init firebase app
// initializeApp(firebaseConfig);

//init services
const db = getFirestore();
const auth = getAuth()

//collection ref
const colRef = collection(db, "accounts");

//queries
//const q = query(colRef, where('firstName', '==', 'Victor'), orderBy('number', 'desc'))
// const q = query(colRef, orderBy('createdAt'))

//get collection data
// getDocs(colRef)
//   .then((snapshot) => {
//     let accounts = [];
//     snapshot.docs.forEach((doc) => {
//       accounts.push({ ...doc.data(), id: doc.id });
//     });
//     console.log(accounts);
//   })
//   .catch((err) => {
//     console.log(err.message);
//   });

  //changes for every collection change //real time collection data
onSnapshot(colRef, (snapshot) => {
  let accounts = [];
  snapshot.docs.forEach((doc) => {
    accounts.push({ ...doc.data(), id: doc.id });
  });
  console.log(accounts);
})

//collection where q
// onSnapshot(q, (snapshot) => {
//   let accounts = [];
//   snapshot.docs.forEach((doc) => {
//     accounts.push({ ...doc.data(), id: doc.id });
//   });
//   console.log(accounts);
// })
const signup = (event) => {
  const signupForm = document.querySelector(".signup");
  event.preventDefault();

  const email = signupForm.email.value
  const password = signupForm.password.value

  createUserWithEmailAndPassword(auth, email, password).then((cred) => {
    console.log('user created:', cred.user)
    signupForm.reset()
  }).catch((err) => {
    console.log(err.message)
  }) 
};

const addAccount = (event) => {
  const addAccountForm = document.querySelector(".add");
  event.preventDefault();

  addDoc(colRef, {
    number: addAccountForm.number.value,
    firstName: addAccountForm.firstName.value,
    lastName: addAccountForm.lastName.value,
    createdAt: serverTimestamp()
  }).then(() => {
    addAccountForm.reset();
  });
};

const deleteAccount = (event) => {
  const deleteAccountForm = document.querySelector(".delete");
  event.preventDefault();

  const docRef = doc(db, "accounts", deleteAccountForm.id.value);

  deleteDoc(docRef).then(() => {
    deleteAccountForm.reset();
  });
};

const updateAccount = (event) => {
  const updateForm = document.querySelector('.update')
  event.preventDefault()

  const docRef = doc(db, 'accounts', updateForm.id.value)

  updateDoc(docRef, {
    number: 1245
  }).then(() => {
    updateForm.reset()
  })

}



//get a single document
const docRef = doc(db, 'accounts', 'z8sDuMDIa3B1gUZiJI1o')

// getDoc(docRef).then((doc) => {
//   console.log(doc.data(), doc.id)
// })

onSnapshot(docRef, (doc) => {
  console.log(doc.data(), doc.id)
})

function App() {
  return (
    <div className="App">
      <h2>Firebase Auth</h2>

<form className="signup">
  <label htmlFor="email">email:</label>
  <input type="email" name="email" required />
  <label htmlFor="password">password:</label>
  <input type="password" name="password" required />
  <button onClick={(event) => signup(event)}>signup</button>
</form>

<h2>Firebase FireStore</h2>

<form className="add">
  <label htmlFor="number">number:</label>
  <input type="number" name="number" required />
  <label htmlFor="firstName">First Name:</label>
  <input type="text" name="firstName" required />
  <label htmlFor="lastName">Last Name:</label>
  <input type="text" name="lastName" required />

  <button onClick={(event) => addAccount(event)}>add new account</button>
</form>

<form className="delete">
  <label htmlFor="id">Document id:</label>
  <input type="text" name="id" required />

  <button onClick={(event) => deleteAccount(event)}>
    delete account
  </button>
</form>

<form className="update">
  <label htmlFor="id">Document id:</label>
  <input type="text" name="id" required />

  <button onClick={(event) => updateAccount(event)}>
    update account
  </button>
</form>
    </div>
  );
}

export default App;
